<script>
export default {
    props: {
        status: {
            type: String,
            default: 'success', // Giá trị mặc định là chuỗi rỗng
        },
        data: {
            type: Object,
            // default: 'success', // Giá trị mặc định là chuỗi rỗng
            require: true,
        },
    },
    computed: {
        card() {
            if (this.status == 'Chưa có GV') {
                return ' card-yellow'
            } else if (this.status == 'Đang khảo sát') {
                return ' card-red'
            } else if (this.status == 'Đang dạy') {
                return ' card-primary'
            } else if (this.status == 'Đã huỷ' || this.status == 'Đã hủy') {
                return ' card-danger'
            } else if (this.status == 'Đã hoàn thành') {
                return ' card-success'
            } else if (this.status == 'Đơn hoàn') {
                return ' card-info'
            } else {
                return ' card-success'
            }
        },

    },
}
</script>

