<script>
import api from '@/store/axios'
import Swal from 'sweetalert2'
import toastr from 'toastr';

export default {
    name: 'DetailServiceInfo',
    data() {
        return {
            noi_dung_khao_sat: null,
            link_edit: null,
            edit_id: null,
            sua_phu_phi_tien: null,
            sua_nap_tien_id: null,
            sua_phu_phi_li_do: null,
            data_hd: null,
            days: [
                { id: 2, value: 'T2' },
                { id: 3, value: 'T3' },
                { id: 4, value: 'T4' },
                { id: 5, value: 'T5' },
                { id: 6, value: 'T6' },
                { id: 7, value: 'T7' },
                { id: 1, value: 'CN' },
            ],
            selectedDays: [],
            date_bd: null,
            buoi: 1,
        };
    },
    props: {
        data: {
            type: Object,
        },
        status: {
            type: String,
            default: '',
        },
        load_data: {
            type: Function,
        },
        nap_tien: {
            type: Array,
        }
    },
    computed: {
        card() {
            console.log(this.status)
            if (this.status == 'Chưa có GV') {
                return ' card-yellow'
            } else if (this.status == 'Đang khảo sát') {
                return ' card-red'
            } else if (this.status == 'Đang dạy') {
                return ' card-primary'
            } else if (this.status == 'Đã huỷ' || this.status == 'Đã hủy') {
                return ' card-danger'
            } else if (this.status == 'Đã hoàn thành') {
                return ' card-success'
            } else if (this.status == 'Đơn hoàn') {
                return ' card-info'
            } else {
                return ' card-success'
            }
        },
        token() {
            const storedUser = JSON.parse(localStorage.getItem('user'));
            return storedUser.auth_key
        }
    },
    methods: {
        activeDay(id) {
            if (this.selectedDays.includes(id)) {
                return id;
            }
            return id + 1000;
        },
        handleClick(index) {
            if (this.selectedDays.includes(index)) {
                this.selectedDays = this.selectedDays.filter(day => day !== index);
            } else {
                this.selectedDays.push(index);
            }
            console.log(this.selectedDays)
        },
        async edit_info() {
            const formData = new FormData()
            formData.append('id', this.data?.id)
            formData.append('thu', this.selectedDays.join(', '))
            formData.append('thoi_gian_bat_dau', this.date_bd)

            await api.post('don-dich-vu/sua-don', formData, {
                'Content-Type': 'multipart/form-data',
                Authorization: 'Bearer ' + this.token
            }).then(res => {
                if (res?.status == 200) {
                    toastr.success(res?.data?.message);
                    this.$refs['my-modal-sua-don'].hide()
                    // window.location.reload();
                    this.load_data()
                }
            })
        },
        sua_phuPhi(id) {
            this.sua_phu_phi_id = id

            api.get(`don-dich-vu/chi-tiet-phu-phi?phu_phi_id=` + id, {
                'Content-Type': 'multipart/form-data',
                Authorization: 'Bearer ' + this.token
            }).then(res => {
                this.sua_phu_phi_tien = res?.data?.data?.tong_tien
                this.sua_nap_tien_id = res?.data?.data?.type_id
                this.sua_phu_phi_li_do = res?.data?.data?.ghi_chu

                this.$refs['my-modal-sua-phu-phi'].show()

            })
        },
        async sua_phu_phi() {

            const formData = new FormData()
            formData.append('phu_phi_id', this.sua_phu_phi_id)
            formData.append('tong_tien', this.sua_phu_phi_tien)
            formData.append('ghi_chu', this.sua_phu_phi_li_do)
            formData.append('type_id', this.sua_nap_tien_id)

            await api.post('don-dich-vu/sua-phu-phi', formData, {
                'Content-Type': 'multipart/form-data',
                Authorization: 'Bearer ' + this.token
            }).then(res => {
                if (res?.status == 200) {
                    toastr.success(res?.data?.message);
                    this.$refs['my-modal-sua-phu-phi'].hide()
                    // window.location.reload();
                    this.load_data()
                }
            })
        },
        show_edit(id) {
            this.edit_id = id
            this.$refs['my-modal-edit'].show()
        },
        async delete_khaosat() {
            const formData = new FormData()
            formData.append('id', this.data?.id)
            formData.append('noi_dung_khao_sat', this.link_edit ?? ' ')

            await api.post('don-dich-vu/cap-nhat-noi-dung-khao-sat', formData, {
                'Content-Type': 'multipart/form-data',
                Authorization: 'Bearer ' + this.token
            }).then(res => {
                if (res?.status == 200) {
                    toastr.success(res?.data?.message);
                    this.$refs['my-modal-edit'].hide()
                    window.location.reload();
                    this.load_data()
                }
            })
        },
        async edit_confirm() {
            if (!this.link_edit) {
                toastr.error('Bạn cần nhập đường dẫn khảo sát');
                return
            }
            const formData = new FormData()
            formData.append('id', this.data?.id)
            formData.append('noi_dung_khao_sat', this.link_edit ?? '')

            await api.post('don-dich-vu/cap-nhat-noi-dung-khao-sat', formData, {
                'Content-Type': 'multipart/form-data',
                Authorization: 'Bearer ' + this.token
            }).then(res => {
                // if (res?.status == 200) {
                    toastr.success('Đã sửa khảo sát');
                    this.$refs['my-modal-edit'].hide()
                    setTimeout(() => {
                        window.location.reload();
                        this.load_data()
                    }, 1500);
                // }
            })
        },
        async khao_sat() {
            if (!this.noi_dung_khao_sat) {
                toastr.error('Bạn cần nhập đường dẫn khảo sát');
                return
            }
            const formData = new FormData()
            formData.append('id', this.data?.id)
            formData.append('noi_dung_khao_sat', this.noi_dung_khao_sat)

            await api.post('don-dich-vu/cap-nhat-noi-dung-khao-sat', formData, {
                'Content-Type': 'multipart/form-data',
                Authorization: 'Bearer ' + this.token
            }).then(res => {
                if (res?.status == 200) {
                    toastr.success(res?.data?.message);
                    this.load_data()
                }
            })
        },
        change_page(content = null) {
            window.open(content, '_blank');
        },
        async delete_phuPhi(id) {
            const formData = new FormData();
            formData.append('phu_phi_id', id)

            Swal.fire({
                title: 'Bạn có chắc chắn?',
                text: `Xoá phụ phí đã chọn!`,
                icon: 'warning',
                showCancelButton: true,
                confirmButtonColor: '#3085d6',
                cancelButtonColor: '#d33',
                confirmButtonText: 'Có xoá nó!',
                cancelButtonText: 'Huỷ'
            }).then(async (result) => {
                if (result.isConfirmed) {
                    await api.post('don-dich-vu/xoa-phu-phi', formData, {
                        'Content-Type': 'multipart/form-data',
                        Authorization: 'Bearer ' + this.token
                    }).then(res => {
                        if (res?.status == 200) {
                            // toastr.success(res?.data?.message);
                            Swal.fire(
                                'Deleted!',
                                res?.data?.message,
                                'success'
                            )
                            // window.location.reload();
                            this.load_data()
                        } else {
                            toastr.error(res?.data?.message);
                        }
                    })

                }
            })

        },
    },
    mounted() {
        this.link_edit = this.data?.noi_dung_khao_sat
        this.noi_dung_khao_sat = this.data?.noi_dung_khao_sat
        this.buoi = this.data?.soBuoiHoanThanh.split('/')[0] ?? 1
        console.log(this.data?.soBuoiHoanThanh.split('/'))

        if (this.data) {
            api.get(`don-dich-vu/get-hoa-don?id=` + this.data?.id, {
                'Content-Type': 'multipart/form-data',
                Authorization: 'Bearer ' + this.token
            }).then(res => {
                this.data_hd = res?.data?.data
            })

            let arr = []
            this.data?.lich_hoc_arr.forEach(element => {
                arr.push(parseInt(element))
            });
            this.selectedDays = arr
        }

    }
}
</script>

