// import WOW from 'wowjs';
// import 'sweetalert2/src/sweetalert2.scss'
export default {
    name: 'DefaultLayout',
    middleware: 'checkLogin',
    data() {
        return {
            visible: true,
            admins: [
                ['Management', 'mdi-account-multiple-outline'],
                ['Settings', 'mdi-cog-outline'],
            ],
            cruds: [
                ['Create', 'mdi-plus-outline'],
                ['Read', 'mdi-file-outline'],
                ['Update', 'mdi-update'],
                ['Delete', 'mdi-delete'],
            ],
            text: null,
            clipped: false,
            drawer: false,
            fixed: false,
            items: [
                {
                    icon: 'mdi-apps',
                    title: 'Welcome',
                    to: '/',
                },
                {
                    icon: 'mdi-chart-bubble',
                    title: 'Inspire',
                    to: '/inspire',
                },
                {
                    icon: 'mdi-chart-bubble',
                    title: 'Login',
                    to: '/login',
                },
            ],
            miniVariant: false,
            right: true,
            rightDrawer: false,
            title: 'Vuetify.js',
            menuItems: [
                {
                    id: 1,
                    label: 'Menu 1',
                    isActive: false,
                    subMenu: [
                        { id: 2, label: 'Submenu 1.1' },
                        { id: 3, label: 'Submenu 1.2' },
                    ],
                },
                {
                    id: 4,
                    label: 'Menu 2',
                    isActive: false,
                    subMenu: [
                        { id: 5, label: 'Submenu 2.1' },
                        { id: 6, label: 'Submenu 2.2' },
                    ],
                },
            ],
            activeItem: null,
            timeOut: null,
            timer: 500,
            searchs: null,
            tuKhoa1: '',
            tuKhoa2: '',
        }
    },
    methods: {
        toggleActive(itemId) {
            this.menuItems.forEach((item) => {
                if (item.id === itemId) {
                    item.isActive = !item.isActive;
                } else {
                    item.isActive = false;
                }
            });
        },
        setActive(itemId) {
            this.activeItem = itemId;
        },
        isCurrentRouteMatching(targetRoute) {
            // Sử dụng $route.path để lấy path của route hiện tại
            return this.$route.path.startsWith(targetRoute);
        },
        async logout() {
            try {
                await localStorage.removeItem('user');
                await localStorage.removeItem('timeLogin');
                toastr.error('Đăng xuất tài khoản thành công')
                await this.$router.push('/login');
                console.log('log')

            } catch (error) {
                if (error?.response?.status != 200) {
                    toastr.error(error?.response?.data?.message)
                }
            }
        },
    },
    mounted() {
        if (window.innerWidth > 800) {
            this.drawer = !this.drawer
        }
    },
    components: {
        RightSvg,
        Avatar,
        AvatarSiderbar
    },
    computed: {
        ready() {
            return this.$store.getters[`title/title`]
        },
        user() {
            const storedUser = JSON.parse(localStorage.getItem('user'));
            return storedUser;
        },
        token() {
            const storedUser = JSON.parse(localStorage.getItem('user'));
            return storedUser.auth_key
        }
    },
    watch: {
        tuKhoa1() {
            if (this.tuKhoa == '') {
                return
            }
            clearTimeout(this.timeOut);
            this.timeOut = setTimeout(() => {
                this.$store.dispatch('parent/set_keyword', this.tuKhoa1);
            }, this.timer);
        },
        tuKhoa2() {
            if (this.tuKhoa == '') {
                return
            }
            clearTimeout(this.timeOut);
            this.timeOut = setTimeout(() => {
                this.$store.dispatch('parent/set_keyword', this.tuKhoa2);
            }, this.timer);
        },
    },
    head() {
