<script>
import SUNEDITOR from 'suneditor'
import plugins from 'suneditor/src/plugins'
import 'suneditor/dist/css/suneditor.min.css'
import api from '@/store/axios'
import Swal from 'sweetalert2'
import toastr from 'toastr';

export default {
    layout: 'admin',
    data() {
        return {
            title: {
                name: 'Lịch sử bàn giao',
                previous: '/admin/materials'
            },
            selected: 0,
            options: [
                { value: 0, text: 'Khuyến mại' },
                { value: 1, text: 'Đơn hàng' },
                { value: 2, text: 'Kết quả đào tạo' },
                { value: 3, text: 'Khác' }
            ],
            data: null,
        };
    },
    computed: {
        id() {
            return this.$route.params.id
        },
        token() {
            const storedUser = JSON.parse(localStorage.getItem('user'));
            return storedUser.auth_key
        }
    },
    methods: {
        async load_data() {
            await api.get('giao-cu/danh-sach-ban-giao?page=1&limit=1000&sort=1&tuKhoa=&giao_cu_id=' + this.id, {
                'Content-Type': 'multipart/form-data',
                Authorization: 'Bearer ' + this.token
            }).then(res => {
                const user = res?.data?.data
                this.data = user
            })
        },
    },
    mounted() {
        this.load_data()
        this.title.previous = '/admin/materials/detail/' + this.id
        this.$store.dispatch('title/set_title', this.title);
    },
    components: {}
}
</script>

