<script>
import api from '@/store/axios'
import Swal from 'sweetalert2'
import toastr from 'toastr';

export default {
    layout: 'admin',
    data() {
        return {
            title: {
                name: 'Chi tiết kết quả',
                previous: '/admin/user/teachers/' + (this.id ?? 0) + '/training-results'
            },
            data: null,
            trang_thai: '',
            ghi_chu: null,
        };
    },

    validate({ params }) {
        // return /^[0-9]{0,2}$/.test(params.id)
        const idRegex = /^\d+$/;
        return (
            idRegex.test(params.id) &&
            idRegex.test(params.results_id)
        );
    },
    computed: {
        id() {
            return this.$route.params.id
        },
        results_id() {
            return this.$route.params.results_id
        },
        token() {
            const storedUser = JSON.parse(localStorage.getItem('user'));
            return storedUser.auth_key
        }
    },
    methods: {
        async load_data() {
            await api.get(`giao-vien/ket-qua-dao-tao?ket_qua_id=` + this.results_id, {
                'Content-Type': 'multipart/form-data',
                Authorization: 'Bearer ' + this.token
            }).then(res => {
                this.data = res?.data?.data
                this.ghi_chu = res?.data?.data?.ghi_chu
                this.trang_thai = res?.data?.data?.trang_thai?.name
            })
        },
        async send_data(event) {
            // event.preventDefault();
            const formData = new FormData()
            formData.append('ket_qua_id', this.results_id)
            formData.append('trang_thai', this.trang_thai)
            formData.append('ghi_chu', this.ghi_chu)

            await api.post('dao-tao/danh-gia-ket-qua', formData, {
                'Content-Type': 'multipart/form-data',
                Authorization: 'Bearer ' + this.token
            }).then(res => {
                if (res?.status == 200) {
                    toastr.success(res?.data?.message);
                    this.load_data();
                }
            })
        },
        async learn_again() {
            const formData = new FormData();
            formData.append('ket_qua_id', this.results_id)
            formData.append('trang_thai', this.trang_thai ?? 'Chưa đạt')
            formData.append('ghi_chu', this.ghi_chu ?? 'Chưa đạt')

            Swal.fire({
                title: 'Bạn có chắc chắn cho ' + this?.data?.giaoVien?.hoten + ' học lại ?',
                text: `Học lại!`,
                icon: 'warning',
                showCancelButton: true,
                confirmButtonColor: '#3085d6',
                cancelButtonColor: '#d33',
                confirmButtonText: 'Xác nhận!',
                cancelButtonText: 'Huỷ'
            }).then(async (result) => {
                if (result.isConfirmed) {
                    await api.post('dao-tao/yeu-cau-hoc-lai', formData, {
                        'Content-Type': 'multipart/form-data',
                        Authorization: 'Bearer ' + this.token
                    }).then(res => {
                        if (res?.status == 200) {
                            // toastr.success(res?.data?.message);
                            Swal.fire(
                                'Thông báo!',
                                res?.data?.message,
                                'success'
                            )
                            this.load_data()
                            this.$router.push('/admin/users/teachers/' + (this.id ?? 0) + '/training-results')
                        } else {
                            toastr.error(res?.data?.message);
                        }
                    })

                }
            })
        },
    },
    mounted() {
        this.title.previous = '/admin/users/teachers/' + (this.id ?? 0) + '/training-results'
        this.load_data()
        this.$store.dispatch('title/set_title', this.title);
    },
}
</script>

